import React, { useEffect, useState } from "react";

const Dashboard = () => {
  const [machines, setMachines] = useState([]);
  const [users, setUsers] = useState([]);   // always array
  const [currentUserId, setCurrentUserId] = useState(null);

  // Format seconds into minutes
  const fmt = (sec) => `${Math.ceil(sec / 60)} min`;

  // Calculate remaining seconds
  const remainingSeconds = (endTime) => {
    if (!endTime) return 0;
    const diff = (new Date(endTime).getTime() - new Date().getTime()) / 1000;
    return diff > 0 ? Math.floor(diff) : 0;
  };

  // Fetch machines
  const fetchMachines = () => {
    fetch("http://localhost:8080/api/machines")
      .then((res) => res.json())
      .then(setMachines)
      .catch((e) => console.error("fetch machines", e));
  };

  // Fetch users
  const fetchUsers = () => {
    fetch("http://localhost:8080/api/users")
      .then((res) => res.json())
      .then((data) => {
        // if backend returns single object instead of array
        if (Array.isArray(data)) {
          setUsers(data);
        } else {
          setUsers([]);
        }
      })
      .catch((e) => {
        console.error("fetch users", e);
        setUsers([]);
      });
  };

  // Add new user
  const addUser = () => {
    const name = prompt("Enter new username:");
    if (!name) return;
    if (users.some((u) => u.name === name)) {
      alert("Username already exists!");
      return;
    }
    fetch("http://localhost:8080/api/users", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ name }),
    })
      .then((res) => res.json())
      .then((u) => {
        setUsers((prev) => [...prev, u]);
        setCurrentUserId(u.id);
      });
  };

  // Join Queue
  const joinQueue = async (machineId) => {
    const minutes = parseInt(prompt("Enter washing time (minutes):"), 10);
    if (!minutes || minutes <= 0) return;

    const res = await fetch(`http://localhost:8080/api/machines/queue/${machineId}`);
    const queue = await res.json();

    const machine = machines.find((m) => m.id === machineId);
    let totalWait = 0;

    if (machine.inUse && machine.endTime) {
      totalWait += remainingSeconds(machine.endTime);
    }
    queue.forEach((q) => (totalWait += q.minutes * 60));

    if (!window.confirm(`Total wait time before your turn: ${fmt(totalWait)}. Join queue with ${minutes} min?`)) {
      return;
    }

    await fetch("http://localhost:8080/api/machines/join", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ machineId, userId: currentUserId, minutes }),
    });
    fetchMachines();
  };

  // Start Washing
  const startWashing = async (machineId) => {
    const minutes = parseInt(prompt("Enter washing time (minutes):"), 10);
    if (!minutes || minutes <= 0) return;

    await fetch("http://localhost:8080/api/machines/start", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ machineId, userId: currentUserId, minutes }),
    });
    fetchMachines();
  };

  useEffect(() => {
    fetchMachines();
    fetchUsers();
    const id = setInterval(fetchMachines, 5000);
    return () => clearInterval(id);
  }, []);

  return (
    <div>
      <h2>WashSimple Dashboard</h2>

      {/* Users */}
      <div>
        <h3>Users</h3>
        <button onClick={addUser}>âž• Add User</button>
        <ul>
          {Array.isArray(users) && users.length > 0 ? (
            users.map((u) => (
              <li key={u.id}>
                <button onClick={() => setCurrentUserId(u.id)}>
                  {u.name} {u.id === currentUserId ? "(Selected)" : ""}
                </button>
              </li>
            ))
          ) : (
            <li>No users found</li>
          )}
        </ul>
      </div>

      {/* Machines */}
      <div className="machines">
        {machines.map((m) => {
          const isOwner = Number(m.currentUserId) === currentUserId;

          // Remaining calculation
          let totalRemainingSec = 0;
          if (m.inUse && m.endTime) {
            totalRemainingSec += remainingSeconds(m.endTime);
          }
          if (m.queue?.length) {
            m.queue.forEach((q) => (totalRemainingSec += q.minutes * 60));
          }

          const machineOccupied = totalRemainingSec > 0;

          const userInAnyMachine = machines.some(
            (mm) =>
              Number(mm.currentUserId) === currentUserId ||
              (mm.queue && mm.queue.some((q) => q.userId === currentUserId))
          );

          return (
            <div className="card" key={m.id}>
              <h4>{m.name}</h4>
              <div>
                <b>Status:</b> {machineOccupied ? "Occupied" : "Free"}
              </div>
              <div>
                <b>Remaining:</b>{" "}
                {totalRemainingSec > 0 ? fmt(totalRemainingSec) : "-"}
              </div>
              <div>
                <b>Queue:</b> {m.queue?.length || 0}
              </div>
              <div style={{ marginTop: 10 }}>
                <button
                  onClick={() => joinQueue(m.id)}
                  disabled={userInAnyMachine || isOwner}
                >
                  Join Queue
                </button>
                <span style={{ marginLeft: 8 }} />
                <button
                  onClick={() => startWashing(m.id)}
                  disabled={userInAnyMachine && !isOwner}
                >
                  {isOwner ? "Start/Extend" : "Start"}
                </button>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Dashboard;
