import React, { useEffect, useState, useRef } from "react";
import SockJS from "sockjs-client";
import { Client } from "@stomp/stompjs";
import "./App.css";

export default function Dashboard() {
  const [machines, setMachines] = useState([]);
  const [users, setUsers] = useState([{ id: 1, name: "User1" }]);
  const [currentUserId, setCurrentUserId] = useState(1);
  const [notifications, setNotifications] = useState([]);
  const [completedWashes, setCompletedWashes] = useState(new Set());
  const clientRef = useRef(null);
  const intervalRef = useRef(null);

  useEffect(() => {
    fetch("http://localhost:8080/api/machines/reset", {
      method: "POST",
      headers: { "Content-Type": "application/json" }
    }).catch(e => console.error("Reset failed", e));

    const socket = new SockJS("http://localhost:8080/ws");
    const client = new Client({
      webSocketFactory: () => socket,
      reconnectDelay: 5000,
      onConnect: () => {
        console.log("WebSocket connected");

        client.subscribe("/topic/machines", (msg) => {
          try {
            setMachines(JSON.parse(msg.body));
          } catch (e) {
            console.error("parse machines", e);
          }
        });

        client.subscribe("/topic/notifications", (msg) => {
          try {
            const payload = JSON.parse(msg.body);
            handleNotification(payload);
          } catch (e) {
            console.error("parse notification", e);
          }
        });

        client.subscribe("/topic/washHistory", (msg) => {
          try {
            const completedUsers = JSON.parse(msg.body);
            setCompletedWashes(new Set(completedUsers));
          } catch (e) {
            console.error("parse wash history", e);
          }
        });
      },
      onStompError: (frame) => {
        console.error("STOMP error", frame);
      }
    });
    client.activate();
    clientRef.current = client;

    fetchMachines();

    intervalRef.current = setInterval(() => {
      setMachines(prev => [...prev]);
    }, 1000);

    if (typeof Notification !== "undefined" && Notification.permission === "default") {
      Notification.requestPermission();
    }

    return () => {
      if (clientRef.current) clientRef.current.deactivate();
      if (intervalRef.current) clearInterval(intervalRef.current);
    };
  }, []);

  function fetchMachines() {
    fetch("http://localhost:8080/api/machines")
      .then(r => {
        if (!r.ok) throw new Error("Network response was not ok");
        return r.json();
      })
      .then(data => setMachines(data))
      .catch(e => console.error("fetch machines", e));
  }

  function handleNotification(payload) {
    setNotifications(prev => [
      ...prev,
      { id: Date.now(), message: payload.message }
    ]);
    setTimeout(() => {
      setNotifications(prev => prev.slice(1));
    }, 5000);
    if (typeof Notification !== "undefined" && Notification.permission === "granted") {
      new Notification(payload.message);
    }
  }

  function remainingSeconds(machine) {
    if (!machine.endTime) return 0;
    return Math.max(0, Math.floor((new Date(machine.endTime) - new Date()) / 1000));
  }

  function fmtSeconds(sec) {
    const m = Math.floor(sec / 60);
    const s = sec % 60;
    return `${m}m ${s}s`;
  }

  const currentUserQueues = machines.filter(m =>
    Number(m.currentUserId) === Number(currentUserId) ||
    (m.queue && m.queue.some(q => Number(q.userId) === Number(currentUserId)))
  );

  return (
    <div className="dashboard-wrapper">
      <div className="container">
        {/* mobile toggle checkbox */}
        <input type="checkbox" id="queue-toggle" />
        <label htmlFor="queue-toggle" className="burger-btn">â˜° Queues</label>

        {/* notifications */}
        {notifications.length > 0 && (
          <div className="notifications">
            {notifications.map(notif => (
              <div key={notif.id} className="notif-card">
                <div className="notif-row">
                  <span className="notif-icon">ðŸ””</span>
                  <span>{notif.message}</span>
                </div>
              </div>
            ))}
          </div>
        )}

        {/* header */}
        <header className="header">
          <h1>Smart Wash Dashboard</h1>
        </header>

        <div className="main-content">
          {/* Machine cards */}
          <div className="machine-grid">
            {machines.map(m => (
              <div key={m.id} className="machine-card">
                <h3>{m.name}</h3>
                {m.inUse ? (
                  <p>In Use â€” {fmtSeconds(remainingSeconds(m))} left</p>
                ) : (
                  <p>Available</p>
                )}
              </div>
            ))}
          </div>

          {/* queue panel */}
          <div className="queue-panel">
            <label htmlFor="queue-toggle" className="close-btn">âœ•</label>
            <h3>All Queues</h3>
            {machines.length === 0 ? (
              <div className="queue-empty">No machines available</div>
            ) : (
              machines.map(m => (
                <div key={m.id} className="queue-machine">
                  <strong>{m.name}</strong>
                  {!m.inUse && (!m.queue || m.queue.length === 0) ? (
                    <div className="queue-no-users">No active users</div>
                  ) : (
                    <div className="queue-details">
                      {m.inUse && (
                        <div className="queue-current-user">
                          ðŸ”„ {users.find(u => u.id === m.currentUserId)?.name || `User${m.currentUserId}`}
                          <div className="queue-current-user-time">
                            {fmtSeconds(remainingSeconds(m))}
                          </div>
                        </div>
                      )}
                      {m.queue?.map((q, idx) => (
                        <div key={q.userId} className="queue-item">
                          #{idx + 1} {users.find(u => u.id === q.userId)?.name || `User${q.userId}`}
                          <div className="queue-item-time">
                            {q.minutes} minutes scheduled
                          </div>
                        </div>
                      ))}
                    </div>
                  )}
                </div>
              ))
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
