import React, { useEffect, useState, useRef } from "react";
import { Client } from "@stomp/stompjs";
import SockJS from "sockjs-client";

export default function Dashboard() {
  const [machines, setMachines] = useState([]);
  const [users, setUsers] = useState([{ id: Math.floor(Math.random() * 100000), name: "User1" }]);
  const [currentUserId, setCurrentUserId] = useState(users[0].id);
  const clientRef = useRef(null);

  // WebSocket setup
  useEffect(() => {
    const client = new Client({
      webSocketFactory: () => new SockJS("http://localhost:8080/ws"),
      reconnectDelay: 5000,
      onConnect: () => {
        client.subscribe("/topic/machines", (msg) => {
          try {
            setMachines(JSON.parse(msg.body));
          } catch (e) { console.error(e); }
        });
      }
    });
    client.activate();
    clientRef.current = client;

    // initial fetch
    fetch("http://localhost:8080/api/machines")
      .then(r => r.json())
      .then(setMachines)
      .catch(console.error);

    return () => { if (clientRef.current) clientRef.current.deactivate(); }
  }, []);

  // format remaining seconds
  function remainingSeconds(endTime) {
    if (!endTime) return 0;
    const diff = Math.floor(new Date(endTime).getTime() / 1000 - Date.now() / 1000);
    return diff > 0 ? diff : 0;
  }
  function fmt(sec) { const m = Math.floor(sec / 60); const s = sec % 60; return `${m}m ${s}s`; }

  // create machines dynamically
  async function createMachines() {
    const countStr = prompt("How many machines to create?", "5");
    const count = parseInt(countStr, 10);
    if (!count || count <= 0) return;
    const res = await fetch("http://localhost:8080/api/machines/create", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ count })
    });
    const data = await res.json();
    setMachines(data);
  }

  // Add new user
  function addUser() {
    const username = prompt("Enter new username:");
    if (!username) return;

    // Check if username already exists
    if (users.some(u => u.name.toLowerCase() === username.toLowerCase())) {
      alert("Username already exists! Choose another.");
      return;
    }

    const newUser = { id: Math.floor(Math.random() * 100000), name: username };
    setUsers([...users, newUser]);
    setCurrentUserId(newUser.id); // switch to new user automatically
  }

  // join queue
async function joinQueue(machineId) {
  // Get machine data from current state
  const machine = machines.find(m => m.id === machineId);
  if (!machine) return;

  // Fetch backend queue
  const resQueue = await fetch(`http://localhost:8080/api/machines/queue/${machineId}`);
  const queueData = await resQueue.json(); // [{userId, minutes}]

  // Calculate wait time
  let waitTime = 0;
  const rem = remainingSeconds(machine.endTime);
  if (machine.inUse) waitTime += rem;

  queueData.forEach(entry => {
    waitTime += entry.minutes || 50; // use 50 as default if not set
  });

  // Ask user to confirm
  const confirmJoin = window.confirm(
    `Current queue has ${queueData.length} users. Total wait time before your turn: ${waitTime} minutes. Join?`
  );

  if (!confirmJoin) return;

  // If confirmed, join
  const res = await fetch("http://localhost:8080/api/machines/join", {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({ machineId, userId: currentUserId })
  });

  const data = await res.json();
  if (data.error) alert("Error: " + data.error);
  else alert(`You joined the queue! Position: ${data.position}, Estimated wait: ${waitTime} min`);
}


  // start washing
  async function startWashing(machineId) {
    const minutesStr = prompt("Enter wash duration in minutes:", "50");
    if (!minutesStr) return;
    const minutes = parseInt(minutesStr, 10) || 50;
    const res = await fetch("http://localhost:8080/api/machines/start", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ machineId, userId: currentUserId, minutes })
    });
    const data = await res.json();
    if (data.error) alert("Error: " + data.error);
    else if (data.started) alert("Started! Ends at: " + data.endTime);
  }

  return (
    <div className="container">
      <div className="header">
        <div>
          <h3>WashSimple</h3>
          <div>
            <b>Current User:</b>{" "}
            <select value={currentUserId} onChange={(e) => setCurrentUserId(Number(e.target.value))}>
              {users.map(u => (
                <option key={u.id} value={u.id}>{u.name}</option>
              ))}
            </select>
            <button onClick={addUser} style={{ marginLeft: 8 }}>Add User</button>
          </div>
        </div>
        <div>
          <small>Open multiple tabs to test realtime sync</small>
        </div>
        <button onClick={createMachines}>Create Machines</button>
      </div>

      <div className="machine-grid">
        {machines.map(m => {
          const rem = remainingSeconds(m.endTime);
          const isOwner = m.currentUserId === currentUserId;
          return (
            <div className="card" key={m.id}>
              <h4>{m.name}</h4>
              <div><b>Status:</b> {m.inUse ? "Running" : "Free"}</div>
              <div><b>Queue:</b> {m.queueSize}</div>
              {m.inUse && <div><b>Remaining:</b> {fmt(rem)}</div>}
              <div style={{ marginTop: 10 }}>
                <button onClick={() => joinQueue(m.id)} disabled={isOwner || !m.inUse}>Join Queue</button>
                <button onClick={() => startWashing(m.id)} disabled={m.inUse && !isOwner} style={{ marginLeft: 8 }}>
                  {isOwner ? "Start/Extend" : "Start"}
                </button>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}
