import React, { useEffect, useState, useRef } from "react";
import "./App.css";

export default function Dashboard() {
  const [machines, setMachines] = useState([]);
  const [users, setUsers] = useState([{ id: 1, name: "User1" }]);
  const [currentUserId, setCurrentUserId] = useState(1);
  const wsRef = useRef(null);
  const intervalRef = useRef(null);
  const reconnectTimeoutRef = useRef(null);

  useEffect(() => {
    connectWebSocket();
    fetchMachines();

    // Auto-refresh remaining time every second
    intervalRef.current = setInterval(() => {
      setMachines(prev => [...prev]);
    }, 1000);

    // Request notification permission
    if (typeof Notification !== "undefined" && Notification.permission === "default") {
      Notification.requestPermission();
    }

    return () => {
      if (wsRef.current) {
        wsRef.current.close();
      }
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
      if (reconnectTimeoutRef.current) {
        clearTimeout(reconnectTimeoutRef.current);
      }
    };
  }, []);

  function connectWebSocket() {
    try {
      const ws = new WebSocket("ws://localhost:8080/ws");

      ws.onopen = () => {
        console.log("WebSocket connected");
        ws.send("CONNECT\naccept-version:1.0,1.1,2.0\n\n\0");
      };

      ws.onmessage = (event) => {
        const message = event.data;

        if (message.startsWith("CONNECTED")) {
          console.log("STOMP connected");
          ws.send("SUBSCRIBE\nid:sub-0\ndestination:/topic/machines\n\n\0");
          ws.send("SUBSCRIBE\nid:sub-1\ndestination:/topic/notifications\n\n\0");
          return;
        }

        if (message.startsWith("MESSAGE")) {
          try {
            const lines = message.split('\n');
            let destination = '';
            let bodyStartIndex = -1;

            for (let i = 0; i < lines.length; i++) {
              if (lines[i].startsWith('destination:')) {
                destination = lines[i].substring(12);
              }
              if (lines[i] === '') {
                bodyStartIndex = i + 1;
                break;
              }
            }

            if (bodyStartIndex > -1) {
              const body = lines.slice(bodyStartIndex).join('\n').replace(/\0$/, '');
              const data = JSON.parse(body);

              if (destination === '/topic/machines') {
                setMachines(data);

                // Sync users from machine data
                const allUserIds = new Set();
                data.forEach(m => {
                  if (m.currentUserId) allUserIds.add(m.currentUserId);
                  if (m.queue) {
                    m.queue.forEach(q => allUserIds.add(q.userId));
                  }
                });

                setUsers(prevUsers => {
                  const existingIds = new Set(prevUsers.map(u => u.id));
                  const newUsers = [...prevUsers];

                  allUserIds.forEach(uid => {
                    if (!existingIds.has(uid)) {
                      newUsers.push({ id: uid, name: `User${uid}` });
                    }
                  });

                  return newUsers;
                });
              } else if (destination === '/topic/notifications') {
                handleNotification(data);
              }
            }
          } catch (e) {
            console.error("Error parsing message:", e);
          }
        }
      };

      ws.onerror = (error) => {
        console.error("WebSocket error:", error);
      };

      ws.onclose = () => {
        console.log("WebSocket closed. Reconnecting...");
        reconnectTimeoutRef.current = setTimeout(() => {
          connectWebSocket();
        }, 5000);
      };

      wsRef.current = ws;
    } catch (error) {
      console.error("Error connecting WebSocket:", error);
      reconnectTimeoutRef.current = setTimeout(() => {
        connectWebSocket();
      }, 5000);
    }
  }

  function fetchMachines() {
    fetch("http://localhost:8080/api/machines")
      .then((r) => {
        if (!r.ok) throw new Error('Network response was not ok');
        return r.json();
      })
      .then((data) => {
        setMachines(data);

        // Sync users from machine data with local users
        const allUserIds = new Set();
        data.forEach(m => {
          if (m.currentUserId) allUserIds.add(m.currentUserId);
          if (m.queue) {
            m.queue.forEach(q => allUserIds.add(q.userId));
          }
        });

        // Add any missing users to the users list
        setUsers(prevUsers => {
          const existingIds = new Set(prevUsers.map(u => u.id));
          const newUsers = [...prevUsers];

          allUserIds.forEach(uid => {
            if (!existingIds.has(uid)) {
              newUsers.push({ id: uid, name: `User${uid}` });
            }
          });

          return newUsers;
        });
      })
      .catch((e) => console.error("fetch machines", e));
  }

  function remainingSeconds(endTime) {
    if (!endTime) return 0;
    const diff = Math.floor(new Date(endTime).getTime() / 1000 - Date.now() / 1000);
    return diff > 0 ? diff : 0;
  }

  function fmtSeconds(totalSec) {
    if (totalSec <= 0) return "0m 0s";
    const mins = Math.floor(totalSec / 60);
    const secs = totalSec % 60;
    return `${mins}m ${secs}s`;
  }

  function handleNotification(payload) {
    if (!payload || payload.type !== "PRE_NOTIFY") return;
    if (Number(payload.userId) !== Number(currentUserId)) return;

    const mins = payload.minutesUntilStart;
    const machineName = payload.machineName || `Machine ${payload.machineId}`;

    alert(`üîî Hey! Your turn on ${machineName} will start in about ${mins} minute(s). Get ready!`);

    if (typeof Notification !== "undefined" && Notification.permission === "granted") {
      new Notification("WashSimple", {
        body: `Your turn on ${machineName} starts in ${mins} minute(s)!`,
        icon: "üîî"
      });
    }
  }

  function addNewUser() {
    const name = prompt("Enter new username:");
    if (!name || !name.trim()) return;
    if (users.some(u => u.name === name.trim())) {
      alert("Username already exists!");
      return;
    }
    const id = Date.now();
    setUsers(prev => [...prev, { id, name: name.trim() }]);
    setCurrentUserId(id);
  }

  function totalRemainingSecondsForMachine(m) {
    let total = 0;
    if (m.inUse && m.endTime) total += remainingSeconds(m.endTime);
    if (m.queue?.length) {
      m.queue.forEach(q => {
        total += (q.minutes || 50) * 60;
      });
    }
    return total;
  }

  function remainingSecondsForUser(m, userId) {
    let total = 0;

    if (m.inUse && Number(m.currentUserId) === Number(userId)) {
      return remainingSeconds(m.endTime);
    }

    if (m.inUse && m.endTime) {
      total += remainingSeconds(m.endTime);
    }

    if (m.queue?.length) {
      for (let q of m.queue) {
        if (Number(q.userId) === Number(userId)) break;
        total += (q.minutes || 50) * 60;
      }
    }

    return total;
  }

  function getCurrentUserQueues() {
    // If currentUserId doesn't exist in any machine data, return empty
    const userExistsInMachines = machines.some(m =>
      Number(m.currentUserId) === Number(currentUserId) ||
      (m.queue && m.queue.some(q => Number(q.userId) === Number(currentUserId)))
    );

    if (!userExistsInMachines) {
      return [];
    }

    const userQueues = machines.filter(m => {
      const isCurrentUser = Number(m.currentUserId) === Number(currentUserId);
      const isInQueue = m.queue && m.queue.some(q => Number(q.userId) === Number(currentUserId));
      return isCurrentUser || isInQueue;
    });
    return userQueues;
  }

  async function joinQueue(machineId) {
    const machine = machines.find(m => m.id === machineId);
    if (!machine) return;

    const userInAnyMachine = machines.some(mm =>
      Number(mm.currentUserId) === Number(currentUserId) ||
      (mm.queue && mm.queue.some(q => Number(q.userId) === Number(currentUserId)))
    );

    if (userInAnyMachine) {
      alert("You are already using or queued for another machine.");
      return;
    }

    const minutesStr = prompt("Enter your wash duration (minutes):", "50");
    if (!minutesStr) return;
    const minutes = parseInt(minutesStr, 10);
    if (isNaN(minutes) || minutes <= 0) {
      alert("Please enter a valid number of minutes.");
      return;
    }

    let totalWaitSec = 0;
    if (machine.inUse && machine.endTime) totalWaitSec += remainingSeconds(machine.endTime);
    machine.queue?.forEach(q => totalWaitSec += (q.minutes || 50) * 60);

    const ok = window.confirm(
      `Machine: ${machine.name}\n` +
      `Current queue: ${machine.queue?.length || 0} user(s)\n` +
      `Estimated wait: ${Math.ceil(totalWaitSec/60)} minutes\n\n` +
      `Join queue?`
    );
    if (!ok) return;

    try {
      const res = await fetch("http://localhost:8080/api/machines/join", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ machineId, userId: currentUserId, minutes })
      });
      if (!res.ok) throw new Error('Failed to join queue');
      const j = await res.json();
      if (j.error) {
        alert(j.error);
      } else {
        alert(`Joined queue. Position: ${j.position}. You will be notified 2 minutes before your turn.`);
        fetchMachines();
      }
    } catch (error) {
      console.error("Error joining queue:", error);
      alert("Failed to join queue. Please try again.");
    }
  }

  async function startWashing(machineId) {
    const machine = machines.find(m => m.id === machineId);
    if (!machine) return;

    const minutesStr = prompt("Enter minutes for wash:", "50");
    if (!minutesStr) return;
    const minutes = parseInt(minutesStr, 10);
    if (isNaN(minutes) || minutes <= 0) {
      alert("Please enter a valid number of minutes.");
      return;
    }

    try {
      const res = await fetch("http://localhost:8080/api/machines/start", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ machineId, userId: currentUserId, minutes })
      });
      if (!res.ok) throw new Error('Failed to start washing');
      const j = await res.json();
      if (j.error) {
        alert(j.error);
      } else if (j.started) {
        alert("Started: ends at " + j.endTime);
        fetchMachines();
      }
    } catch (error) {
      console.error("Error starting wash:", error);
      alert("Failed to start washing. Please try again.");
    }
  }

  const currentUserQueues = getCurrentUserQueues();

  return (
    <div className="dashboard-wrapper">
      <div className="container">
        {/* Header */}
        <div className="header-card">
          <div className="header">
            <div className="header-title">
              <span className="header-title-icon">üíß</span>
              <h1>WashSimple</h1>
            </div>
            <div className="header-controls">
              <span className="header-controls-icon">üë§</span>
              <select
                className="user-select"
                value={currentUserId}
                onChange={(e) => setCurrentUserId(Number(e.target.value))}
              >
                {users.map(u => (
                  <option key={u.id} value={u.id}>{u.name}</option>
                ))}
              </select>
              <button className="add-user-btn" onClick={addNewUser}>
                ‚ûï Add User
              </button>
            </div>
          </div>

          {/* Current User Status - Always show */}
          {currentUserQueues.length > 0 ? (
            <div className="active-sessions">
              <div className="active-sessions-label">
                <span className="active-sessions-label-icon">üîî</span>
                <strong>Your Active Sessions:</strong>
              </div>
              {currentUserQueues.map(m => {
                const waitSec = remainingSecondsForUser(m, currentUserId);
                const isWashing = Number(m.currentUserId) === Number(currentUserId) && m.inUse;
                const queueIndex = m.queue?.findIndex(q => Number(q.userId) === Number(currentUserId));

                return (
                  <div
                    key={m.id}
                    className={isWashing ? "session-card session-card-washing" : "session-card session-card-queued"}
                  >
                    <strong>{m.name}</strong>
                    <div className="session-card-details">
                      {isWashing ? (
                        <>üîÑ Washing ‚Ä¢ {fmtSeconds(waitSec)} left</>
                      ) : (
                        <>‚è≥ Position {queueIndex !== undefined ? queueIndex + 1 : 0} ‚Ä¢ ~{Math.ceil(waitSec / 60)} min wait</>
                      )}
                    </div>
                  </div>
                );
              })}
            </div>
          ) : (
            <div className="no-sessions">
              <span className="no-sessions-icon">‚ÑπÔ∏è</span>
              <div className="no-sessions-text">
                <strong>No active sessions</strong>
                <div style={{ marginTop: '4px', fontSize: '13px' }}>
                  Join a queue or start washing to see your session here!
                </div>
              </div>
            </div>
          )}
        </div>

        {/* Main Content */}
        <div className="main-content">
          {/* Machines Grid */}
          <div className="machine-grid">
            {machines.map(m => {
              const totalSec = totalRemainingSecondsForMachine(m);
              const occupied = totalSec > 0;
              const isOwner = Number(m.currentUserId) === Number(currentUserId);
              const userInAnyMachine = machines.some(mm =>
                Number(mm.currentUserId) === Number(currentUserId) ||
                (mm.queue && mm.queue.some(q => Number(q.userId) === Number(currentUserId)))
              );

              return (
                <div key={m.id} className={isOwner ? "card card-owner" : "card"}>
                  <h3>{m.name}</h3>

                  <div className="card-details">
                    <div className="status-row">
                      <div className={occupied ? "status-indicator status-indicator-occupied" : "status-indicator status-indicator-available"} />
                      <strong>{occupied ? "Occupied" : "Available"}</strong>
                    </div>

                    <div className="info-row">
                      <span className="info-icon">‚è∞</span>
                      <span>Remaining: <strong>{fmtSeconds(totalSec)}</strong></span>
                    </div>

                    <div className="info-row">
                      <span className="info-icon">üë•</span>
                      <span>Queue: <strong>{m.queue?.length || 0}</strong></span>
                    </div>
                  </div>

                  <div className="card-actions">
                    <button
                      className="btn-join-queue"
                      onClick={() => joinQueue(m.id)}
                      disabled={userInAnyMachine}
                    >
                      Join Queue
                    </button>
                    <button
                      className="btn-start"
                      onClick={() => startWashing(m.id)}
                      disabled={(!isOwner && userInAnyMachine) || (m.inUse && !isOwner)}
                    >
                      {isOwner ? "Extend" : "Start"}
                    </button>
                  </div>
                </div>
              );
            })}
          </div>

          {/* Right Panel - Queue Details */}
          <div className="queue-panel">
            <h3>All Queues</h3>
            {machines.length === 0 ? (
              <div className="queue-empty">
                No machines available
              </div>
            ) : (
              machines.map(m => (
                <div key={m.id} className="queue-machine">
                  <strong>{m.name}</strong>
                  {!m.inUse && (!m.queue || m.queue.length === 0) ? (
                    <div className="queue-no-users">
                      No active users
                    </div>
                  ) : (
                    <div className="queue-details">
                      {m.inUse && (
                        <div className="queue-current-user">
                          üîÑ {users.find(u => u.id === m.currentUserId)?.name || `User${m.currentUserId}`}
                          <div className="queue-current-user-time">
                            {fmtSeconds(remainingSeconds(m.endTime))} remaining
                          </div>
                        </div>
                      )}
                      {m.queue?.map((q, idx) => (
                        <div key={q.userId} className="queue-item">
                          #{idx + 1} {users.find(u => u.id === q.userId)?.name || `User${q.userId}`}
                          <div className="queue-item-time">
                            {q.minutes} min
                          </div>
                        </div>
                      ))}
                    </div>
                  )}
                </div>
              ))
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
