import React, { useEffect, useState, useRef } from "react";
import SockJS from "sockjs-client";
import { Client } from "@stomp/stompjs";

export default function Dashboard() {
  const [machines, setMachines] = useState([]);
  const [users, setUsers] = useState([{ id: 1, name: "User1" }]);
  const [currentUserId, setCurrentUserId] = useState(1);
  const clientRef = useRef(null);

  useEffect(() => {
    const socket = new SockJS("http://localhost:8080/ws");
    const client = new Client({
      webSocketFactory: () => socket,
      reconnectDelay: 5000,
      onConnect: () => {
        client.subscribe("/topic/machines", (msg) => {
          try {
            setMachines(JSON.parse(msg.body));
          } catch (e) {
            console.error("parse machines", e);
          }
        });

        client.subscribe("/topic/notifications", (msg) => {
          try {
            const payload = JSON.parse(msg.body);
            handleNotification(payload);
          } catch (e) {
            console.error("parse notification", e);
          }
        });
      },
    });
    client.activate();
    clientRef.current = client;

    fetchMachines();

    return () => {
      if (clientRef.current) clientRef.current.deactivate();
    };
  }, []);

  function fetchMachines() {
    fetch("http://localhost:8080/api/machines")
      .then((r) => r.json())
      .then((data) => setMachines(data))
      .catch((e) => console.error("fetch machines", e));
  }

  function remainingSeconds(endTime) {
    if (!endTime) return 0;
    const diff = Math.floor(new Date(endTime).getTime() / 1000 - Date.now() / 1000);
    return diff > 0 ? diff : 0;
  }

  function fmtSeconds(totalSec) {
    if (totalSec <= 0) return "-";
    const mins = Math.floor(totalSec / 60);
    const secs = totalSec % 60;
    return `${mins}m ${secs}s`;
  }

  function handleNotification(payload) {
    if (!payload || payload.type !== "PRE_NOTIFY") return;
    if (Number(payload.userId) !== Number(currentUserId)) return;

    const mins = payload.minutesUntilStart;
    const machineName = payload.machineName || `Machine ${payload.machineId}`;
    alert(
      `ðŸ”” Hey! Your turn on ${machineName} will start in about ${mins} minute(s). Get ready!`
    );
  }

  function addNewUser() {
    const name = prompt("Enter new username:");
    if (!name) return;
    if (users.some((u) => u.name === name)) {
      alert("Username already exists!");
      return;
    }
    const id = Date.now();
    setUsers((prev) => [...prev, { id, name }]);
    setCurrentUserId(id);
  }

  function totalRemainingSecondsForMachine(m) {
    let total = 0;
    if (m.inUse && m.endTime) total += remainingSeconds(m.endTime);
    if (m.queue?.length) {
      m.queue.forEach((q) => {
        total += (q.minutes || 50) * 60;
      });
    }
    return total;
  }

  async function joinQueue(machineId) {
    const machine = machines.find((m) => m.id === machineId);
    if (!machine) return;

    const userInAnyMachine = machines.some(
      (mm) =>
        Number(mm.currentUserId) === currentUserId ||
        (mm.queue && mm.queue.some((q) => q.userId === currentUserId))
    );
    if (userInAnyMachine) {
      alert("You are already using or queued for another machine.");
      return;
    }

    const queueEntries = machine.queue || [];
    let queueStr = "";
    if (machine.inUse && machine.currentUserId) {
      const userObj = users.find((u) => u.id === machine.currentUserId);
      queueStr += `Current: ${
        userObj ? userObj.name : "User" + machine.currentUserId
      } â€” remaining ${fmtSeconds(remainingSeconds(machine.endTime))}\n`;
    }
    queueEntries.forEach((q, idx) => {
      const userObj = users.find((u) => u.id === q.userId);
      queueStr += `${idx + 1}. ${userObj ? userObj.name : "User" + q.userId} â€” ${
        q.minutes
      } min\n`;
    });

    const minutesStr = prompt(
      `Queue:\n${queueStr || "No queued users"}\n\nEnter your wash duration (minutes):`,
      "50"
    );
    if (!minutesStr) return;
    const minutes = parseInt(minutesStr, 10) || 50;

    let totalWaitSec = 0;
    if (machine.inUse && machine.endTime) totalWaitSec += remainingSeconds(machine.endTime);
    queueEntries.forEach((q) => (totalWaitSec += (q.minutes || 50) * 60));

    const ok = window.confirm(
      `Total wait before your turn: ${Math.ceil(totalWaitSec / 60)} minutes.\nJoin queue?`
    );
    if (!ok) return;

    const res = await fetch("http://localhost:8080/api/machines/join", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ machineId, userId: currentUserId, minutes }),
    });
    const j = await res.json();
    if (j.error) alert(j.error);
    else {
      alert(
        `Joined queue. Position: ${j.position}. You will be notified 2 minutes before your turn.`
      );
      fetchMachines();
    }
  }

  async function startWashing(machineId) {
    const minutesStr = prompt("Enter minutes for wash (minutes)", "50");
    if (!minutesStr) return;
    const minutes = parseInt(minutesStr, 10) || 50;

    const res = await fetch("http://localhost:8080/api/machines/start", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ machineId, userId: currentUserId, minutes }),
    });
    const j = await res.json();
    if (j.error) alert(j.error);
    else if (j.started) {
      alert("Started: ends at " + j.endTime);
      fetchMachines();
    }
  }

  return (
    <div className="container" style={{ display: "flex", gap: 20 }}>
      {/* Left side: Machine cards */}
      <div style={{ flex: 2 }}>
        <div
          className="header"
          style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}
        >
          <div>
            <h3>WashSimple â€” user: {users.find((u) => u.id === currentUserId)?.name}</h3>
          </div>
          <div>
            <button onClick={addNewUser}>Add New User</button>
          </div>
        </div>

        <div style={{ display: "flex", gap: 12, flexWrap: "wrap", marginTop: 16 }}>
          {machines.map((m) => {
            const totalSec = totalRemainingSecondsForMachine(m);
            const occupied = m.inUse;
            const isOwner = Number(m.currentUserId) === currentUserId;
            const queue = m.queue || [];
            const isFirstInQueue = queue.length > 0 && queue[0].userId === currentUserId;

            return (
              <div
                key={m.id}
                className="card"
                style={{
                  width: 220,
                  padding: 12,
                  border: "1px solid #ddd",
                  borderRadius: 6,
                  boxShadow: "0 2px 5px rgba(0,0,0,0.1)",
                  backgroundColor: "#fafafa",
                }}
              >
                <h4>{m.name}</h4>
                <div>
                  <b>Status:</b> {occupied ? "Occupied" : "Free"}
                </div>
                <div>
                  <b>Remaining:</b> {fmtSeconds(totalSec)}
                </div>
                <div>
                  <b>Queue:</b> {queue.length}
                </div>
                <div style={{ marginTop: 10 }}>
                  <button onClick={() => joinQueue(m.id)} disabled={isOwner || isFirstInQueue}>
                    Join Queue
                  </button>
                  <button
                    onClick={() => startWashing(m.id)}
                    disabled={!(isOwner || (!occupied && isFirstInQueue))}
                    style={{ marginLeft: 8 }}
                  >
                    {isOwner ? "Start/Extend" : "Start"}
                  </button>
                </div>
              </div>
            );
          })}
        </div>
      </div>

      {/* Right side: Stylish Queue Panel */}
      <div
        style={{
          flex: 1,
          border: "1px solid #ccc",
          borderRadius: 8,
          padding: 12,
          maxHeight: "80vh",
          overflowY: "auto",
          backgroundColor: "#f0f8ff",
        }}
      >
        <h4 style={{ textAlign: "center", marginBottom: 12 }}>Machines Queue Overview</h4>
        {machines.map((m) => {
          const queue = m.queue || [];
          const occupiedSec = remainingSeconds(m.endTime);
          return (
            <div
              key={m.id}
              style={{
                padding: 12,
                border: "1px solid #aaa",
                borderRadius: 8,
                marginBottom: 16,
                backgroundColor: "#ffffff",
                boxShadow: "0 2px 6px rgba(0,0,0,0.1)",
              }}
            >
              <b style={{ fontSize: 16 }}>{m.name}</b>

              {/* Progress bar for current washing */}
              {m.inUse && (
                <div
                  style={{
                    marginTop: 6,
                    padding: "4px 0",
                    backgroundColor: "#e0e0e0",
                    borderRadius: 4,
                  }}
                >
                  <div
                    style={{
                      width: `${Math.min(100, (occupiedSec / 3600) * 100)}%`,
                      backgroundColor: "#4caf50",
                      height: 12,
                      borderRadius: 4,
                      transition: "width 1s linear",
                    }}
                  ></div>
                  <small>
                    {users.find((u) => u.id === m.currentUserId)?.name || "User" + m.currentUserId} - Currently Washing ({fmtSeconds(occupiedSec)})
                  </small>
                </div>
              )}

              {/* Queue users */}
              {queue.map((q, idx) => {
                const isNext = idx === 0;
                return (
                  <div
                    key={q.userId}
                    style={{
                      marginTop: 6,
                      padding: "6px 8px",
                      borderRadius: 6,
                      backgroundColor: isNext ? "#fff3cd" : "#e7f3ff",
                      border: isNext ? "1px solid #ffecb5" : "1px solid #cce5ff",
                      fontWeight: isNext ? "bold" : "normal",
                    }}
                  >
                    {users.find((u) => u.id === q.userId)?.name || "User" + q.userId}{" "}
                    {isNext && "- Next"}
                  </div>
                );
              })}
              {queue.length === 0 && !m.inUse && (
                <div style={{ marginTop: 6, color: "#777" }}>Free</div>
              )}
            </div>
          );
        })}
      </div>
    </div>
  );
}
