import React, { useEffect, useState } from "react";

const Dashboard = () => {
  const [machines, setMachines] = useState([]);

  useEffect(() => {
    const fetchMachines = async () => {
      try {
        const response = await fetch("http://localhost:8080/machines");
        const data = await response.json();
        setMachines(data);
      } catch (error) {
        console.error("Error fetching machines:", error);
      }
    };

    fetchMachines();
    const interval = setInterval(fetchMachines, 1000); // refresh every second
    return () => clearInterval(interval);
  }, []);

  return (
    <div style={{ display: "flex", padding: "20px" }}>
      {/* LEFT SIDE: Machine Status */}
      <div style={{ flex: 2, marginRight: "20px" }}>
        <h2>Dashboard</h2>
        <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: "20px" }}>
          {machines.map((machine) => (
            <div
              key={machine.id}
              style={{
                border: "1px solid #ccc",
                borderRadius: "10px",
                padding: "15px",
                backgroundColor: machine.status === "Occupied" ? "#ffefef" : "#eaffea",
              }}
            >
              <h3>{machine.name}</h3>
              <p>Status: <b>{machine.status}</b></p>
              <p>
                Remaining:{" "}
                {machine.remainingTime > 0
                  ? `${Math.floor(machine.remainingTime / 60)} min ${machine.remainingTime % 60} sec`
                  : "0 min 0 sec"}
              </p>
              <p>Queue: {machine.queue.length}</p>
            </div>
          ))}
        </div>
      </div>

      {/* RIGHT SIDE: Queue Users */}
      <div style={{ flex: 1 }}>
        <h2>Queues</h2>
        {machines.map((machine) => (
          <div
            key={machine.id}
            style={{
              border: "1px solid #ccc",
              borderRadius: "10px",
              padding: "10px",
              marginBottom: "15px",
              backgroundColor: "#f9f9f9",
            }}
          >
            <h3>{machine.name}</h3>
            {machine.queue.length === 0 ? (
              <p>No users in queue</p>
            ) : (
              <ul style={{ listStyle: "none", padding: 0 }}>
                {machine.queue.map((user, index) => (
                  <li
                    key={index}
                    style={{
                      padding: "5px",
                      margin: "5px 0",
                      borderRadius: "6px",
                      backgroundColor:
                        index === 0
                          ? "#ffd6d6" // current user
                          : index === 1
                          ? "#fff6d6" // next user
                          : "#e6e6e6", // rest
                    }}
                  >
                    {index === 0 && (
                      <b>
                        {user} (Current) – Time Left:{" "}
                        {machine.remainingTime > 0
                          ? `${Math.floor(machine.remainingTime / 60)}m ${machine.remainingTime % 60}s`
                          : "0m 0s"}
                      </b>
                    )}
                    {index === 1 && <b>{user} (Next)</b>}
                    {index > 1 && user}
                  </li>
                ))}
              </ul>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Dashboard;
